import React, { createContext, useContext, useEffect, useState, ReactNode, useCallback } from 'react';
import { ethers } from 'ethers';
import { AppState, UserProfile } from '../types';

interface WalletContextType {
  state: AppState;
  connectWallet: () => Promise<void>;
  disconnectWallet: () => void;
  checkRegistration: () => Promise<boolean>;
  registerUser: (username: string, email: string) => Promise<void>;
}

const WalletContext = createContext<WalletContextType | undefined>(undefined);

interface WalletProviderProps {
  children: ReactNode;
}

export const WalletProvider: React.FC<WalletProviderProps> = ({ children }) => {
  const [state, setState] = useState<AppState>({
    wallet: {
      isConnected: false,
      account: null,
      chainId: null,
      balance: null,
    },
    userProfile: null,
    isRegistered: false,
    isLoading: false,
    error: null,
  });

  const [provider, setProvider] = useState<ethers.BrowserProvider | null>(null);
  const [signer, setSigner] = useState<ethers.JsonRpcSigner | null>(null);

  const disconnectWallet = useCallback(() => {
    setState({
      wallet: {
        isConnected: false,
        account: null,
        chainId: null,
        balance: null,
      },
      userProfile: null,
      isRegistered: false,
      isLoading: false,
      error: null,
    });
    setProvider(null);
    setSigner(null);
  }, []);

  const connectWallet = useCallback(async () => {
    try {
      setState(prev => ({ ...prev, isLoading: true, error: null }));

      if (typeof window.ethereum === 'undefined') {
        throw new Error('MetaMask is not installed');
      }

      const newProvider = new ethers.BrowserProvider(window.ethereum);
      setProvider(newProvider);

      const accounts = await window.ethereum.request({
        method: 'eth_requestAccounts',
      });

      if (accounts.length === 0) {
        throw new Error('No accounts found');
      }

      const newSigner = await newProvider.getSigner();
      setSigner(newSigner);

      const network = await newProvider.getNetwork();
      const balance = await newProvider.getBalance(accounts[0]);

      setState(prev => ({
        ...prev,
        wallet: {
          isConnected: true,
          account: accounts[0],
          chainId: network.chainId.toString(),
          balance: ethers.formatEther(balance),
        },
        isLoading: false,
      }));

      // Check if user is registered
      // await checkRegistration();
    } catch (error: any) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error.message || 'Failed to connect wallet',
      }));
    }
  }, []);

  const handleAccountsChanged = useCallback((accounts: string[]) => {
    if (accounts.length === 0) {
      disconnectWallet();
    } else {
      connectWallet();
    }
  }, [connectWallet, disconnectWallet]);

  const handleChainChanged = useCallback(() => {
    window.location.reload();
  }, []);

  useEffect(() => {
    const initWallet = async () => {
      if (typeof window.ethereum !== 'undefined') {
        const newProvider = new ethers.BrowserProvider(window.ethereum);
        setProvider(newProvider);

        // Check if already connected
        try {
          const accounts = await window.ethereum.request({ method: 'eth_accounts' });
          if (accounts.length > 0) {
            await connectWallet();
          }
        } catch (error) {
          console.error('Error checking existing connection:', error);
        }

        // Listen for account changes
        window.ethereum.on('accountsChanged', handleAccountsChanged);
        window.ethereum.on('chainChanged', handleChainChanged);
      }
    };

    initWallet();

    return () => {
      if (window.ethereum) {
        window.ethereum.removeListener('accountsChanged', handleAccountsChanged);
        window.ethereum.removeListener('chainChanged', handleChainChanged);
      }
    };
  }, [connectWallet, handleAccountsChanged, handleChainChanged]);


  const checkRegistration = useCallback(async (): Promise<boolean> => {
    if (!provider || !state.wallet.account) return false;

    try {
      // This would be implemented with actual contract calls
      // For now, we'll simulate it
      const isRegistered = localStorage.getItem(`registered_${state.wallet.account}`) === 'true';
      
      setState(prev => ({
        ...prev,
        isRegistered,
      }));

      return isRegistered;
    } catch (error) {
      console.error('Error checking registration:', error);
      return false;
    }
  }, [provider, state.wallet.account]);

  const registerUser = useCallback(async (username: string, email: string) => {
    if (!provider || !signer || !state.wallet.account) {
      throw new Error('Wallet not connected');
    }

    try {
      setState(prev => ({ ...prev, isLoading: true, error: null }));

      // This would be implemented with actual contract calls
      // For now, we'll simulate registration
      await new Promise(resolve => setTimeout(resolve, 2000));

      localStorage.setItem(`registered_${state.wallet.account}`, 'true');
      
      const userProfile: UserProfile = {
        walletAddress: state.wallet.account,
        username,
        email,
        registrationTime: new Date().toISOString(),
        isActive: true,
        reputationScore: 100,
        accessRoles: [],
      };

      setState(prev => ({
        ...prev,
        userProfile,
        isRegistered: true,
        isLoading: false,
      }));
    } catch (error: any) {
      setState(prev => ({
        ...prev,
        isLoading: false,
        error: error.message || 'Registration failed',
      }));
      throw error;
    }
  }, [provider, signer, state.wallet.account]);

  const value: WalletContextType = {
    state,
    connectWallet,
    disconnectWallet,
    checkRegistration,
    registerUser,
  };

  return (
    <WalletContext.Provider value={value}>
      {children}
    </WalletContext.Provider>
  );
};

export const useWallet = (): WalletContextType => {
  const context = useContext(WalletContext);
  if (context === undefined) {
    throw new Error('useWallet must be used within a WalletProvider');
  }
  return context;
};

// Extend Window interface for TypeScript
declare global {
  interface Window {
    ethereum?: any;
  }
}
