import React, { useState, useEffect } from 'react';
import { 
  User, 
  Shield, 
  Activity, 
  LogOut, 
  Clock,
  CheckCircle,
  XCircle,
  FileText
} from 'lucide-react';
import FileManager from './files/FileManager';
import { useWallet } from '../context/WalletContext';

const Dashboard: React.FC = () => {
  const { 
    wallet, 
    isRegistered, 
    userProfile, 
    disconnectWallet, 
    registerUser, 
    getStoredFiles 
  } = useWallet();
  // Registration form is always shown when user is not registered
  const [registrationData, setRegistrationData] = useState({ username: '', email: '' });
  const [isRegistering, setIsRegistering] = useState(false);
  const [registrationError, setRegistrationError] = useState<string | null>(null);
  const [fileCount, setFileCount] = useState(0);

  // Update file count when wallet account changes
  useEffect(() => {
    if (wallet.account) {
      const files = getStoredFiles();
      setFileCount(files.length);
    } else {
      setFileCount(0);
    }
  }, [wallet.account, getStoredFiles]);

  // Debug logging
  console.log('Dashboard - isRegistered:', isRegistered, 'userProfile:', userProfile);

  const handleRegistration = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!registrationData.username) {
      setRegistrationError('Username is required');
      return;
    }

    setIsRegistering(true);
    setRegistrationError(null);

    try {
      const result = await registerUser(registrationData.username);
      if (result.success) {
        // Clear form data after successful registration
        setRegistrationData({ username: '', email: '' });
      } else {
        setRegistrationError(result.message || 'Registration failed');
      }
    } catch (error) {
      console.error('Registration failed:', error);
      setRegistrationError('An unexpected error occurred during registration');
    } finally {
      setIsRegistering(false);
    }
  };

  const formatAddress = (address: string) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const mockAccessRequests = [
    {
      id: 1,
      resource: 'Database',
      action: 'Read',
      timestamp: new Date().toISOString(),
      status: 'pending'
    },
    {
      id: 2,
      resource: 'API Gateway',
      action: 'Write',
      timestamp: new Date(Date.now() - 86400000).toISOString(),
      status: 'approved'
    },
    {
      id: 3,
      resource: 'File Storage',
      action: 'Delete',
      timestamp: new Date(Date.now() - 172800000).toISOString(),
      status: 'denied'
    }
  ];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'denied':
        return <XCircle className="w-4 h-4 text-red-500" />;
      default:
        return <Clock className="w-4 h-4 text-yellow-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'text-green-400';
      case 'denied':
        return 'text-red-400';
      default:
        return 'text-yellow-400';
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex">
              <div className="flex-shrink-0 flex items-center">
                <span className="text-xl font-bold text-indigo-600">BSCIAM</span>
              </div>
              <nav className="hidden sm:ml-6 sm:flex sm:space-x-8">
                <a
                  href="#"
                  className="border-indigo-500 text-gray-900 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium"
                >
                  Dashboard
                </a>
                <a
                  href="#"
                  className="border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium"
                >
                  Files
                </a>
                <a
                  href="#"
                  className="border-transparent text-gray-500 hover:border-gray-300 hover:text-gray-700 inline-flex items-center px-1 pt-1 border-b-2 text-sm font-medium"
                >
                  Activity
                </a>
              </nav>
            </div>
            <div className="hidden sm:ml-6 sm:flex sm:items-center">
              <div className="ml-3 relative">
                <div>
                  <button
                    type="button"
                    className="bg-white rounded-full flex text-sm focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    id="user-menu-button"
                    aria-expanded="false"
                    aria-haspopup="true"
                  >
                    <span className="sr-only">Open user menu</span>
                    <div className="h-8 w-8 rounded-full bg-indigo-100 flex items-center justify-center">
                      <User className="h-5 w-5 text-indigo-600" />
                    </div>
                  </button>
                </div>
              </div>
              <button
                onClick={disconnectWallet}
                className="ml-4 inline-flex items-center px-3 py-1.5 border border-transparent text-xs font-medium rounded-md shadow-sm text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
              >
                <LogOut className="h-3 w-3 mr-1" />
                Disconnect
              </button>
            </div>
          </div>
        </div>
      </div>

      <div className="py-10">
        <header>
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          </div>
        </header>
        <main>
          <div className="max-w-7xl mx-auto sm:px-6 lg:px-8">
            <div className="px-4 py-8 sm:px-0">
              {!isRegistered ? (
                <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                  <div className="px-4 py-5 sm:p-6">
                    <h3 className="text-lg leading-6 font-medium text-gray-900">
                      Complete your registration
                    </h3>
                    <div className="mt-2 max-w-xl text-sm text-gray-500">
                      <p>Please provide some additional information to complete your registration.</p>
                    </div>
                    <form className="mt-5 space-y-4" onSubmit={handleRegistration}>
                      <div className="space-y-2">
                        <div className="flex flex-col sm:flex-row gap-3">
                          <div className="w-full sm:max-w-xs">
                            <label htmlFor="username" className="block text-sm font-medium text-gray-700 mb-1">
                              Username *
                            </label>
                            <input
                              type="text"
                              name="username"
                              id="username"
                              className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                              placeholder="Choose a username"
                              value={registrationData.username}
                              onChange={(e) => setRegistrationData({...registrationData, username: e.target.value})}
                              required
                            />
                          </div>
                          <div className="w-full sm:max-w-xs">
                            <label htmlFor="email" className="block text-sm font-medium text-gray-700 mb-1">
                              Email (optional)
                            </label>
                            <input
                              type="email"
                              name="email"
                              id="email"
                              className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
                              placeholder="your@email.com"
                              value={registrationData.email}
                              onChange={(e) => setRegistrationData({...registrationData, email: e.target.value})}
                            />
                          </div>
                        </div>
                        {registrationError && (
                          <p className="mt-1 text-sm text-red-600">{registrationError}</p>
                        )}
                      </div>
                      <div className="flex justify-end">
                        <button
                          type="submit"
                          disabled={isRegistering}
                          className={`inline-flex items-center justify-center px-4 py-2 border border-transparent shadow-sm font-medium rounded-md text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:w-auto sm:text-sm ${isRegistering ? 'opacity-50 cursor-not-allowed' : ''}`}
                        >
                          {isRegistering ? (
                            <>
                              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                              </svg>
                              Registering...
                            </>
                          ) : 'Register'}
                        </button>
                      </div>
                    </form>
                  </div>
                </div>
              ) : (
                <div className="space-y-6">
                  {/* Welcome Card */}
                  <div className="bg-white overflow-hidden shadow rounded-lg">
                    <div className="px-4 py-5 sm:p-6">
                      <div className="flex items-center">
                        <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                          <User className="h-6 w-6 text-white" />
                        </div>
                        <div className="ml-5 w-0 flex-1">
                          <dl>
                            <dt className="text-sm font-medium text-gray-500 truncate">
                              Welcome back, {userProfile?.username || 'User'}
                            </dt>
                            <dd>
                              <div className="text-lg font-medium text-gray-900">
                                {wallet.account ? (
                                  <span className="font-mono">{formatAddress(wallet.account)}</span>
                                ) : 'No wallet connected'}
                              </div>
                            </dd>
                          </dl>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* File Manager Section */}
                  <div className="bg-white shadow rounded-lg overflow-hidden">
                    <div className="px-4 py-5 border-b border-gray-200 sm:px-6">
                      <h3 className="text-lg leading-6 font-medium text-gray-900">
                        Secure File Storage
                      </h3>
                      <p className="mt-1 text-sm text-gray-500">
                        Upload and manage your encrypted files. All files are encrypted before storage.
                      </p>
                    </div>
                    <div className="px-4 py-5 sm:p-6">
                      <FileManager walletAddress={wallet.account || ''} />
                    </div>
                  </div>

                  {/* Quick Stats */}
                  <div className="grid grid-cols-1 gap-5 sm:grid-cols-2 lg:grid-cols-3">
                    <div className="bg-white overflow-hidden shadow rounded-lg">
                      <div className="px-4 py-5 sm:p-6">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                            <Shield className="h-6 w-6 text-white" />
                          </div>
                          <div className="ml-5 w-0 flex-1">
                            <dl>
                              <dt className="text-sm font-medium text-gray-500 truncate">Access Level</dt>
                              <dd>
                                <div className="text-lg font-medium text-gray-900">Standard</div>
                              </dd>
                            </dl>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="bg-white overflow-hidden shadow rounded-lg">
                      <div className="px-4 py-5 sm:p-6">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                            <Activity className="h-6 w-6 text-white" />
                          </div>
                          <div className="ml-5 w-0 flex-1">
                            <dl>
                              <dt className="text-sm font-medium text-gray-500 truncate">Reputation</dt>
                              <dd>
                                <div className="text-lg font-medium text-gray-900">
                                  {userProfile?.reputationScore || 100}
                                </div>
                              </dd>
                            </dl>
                          </div>
                        </div>
                      </div>
                    </div>

                    <div className="bg-white overflow-hidden shadow rounded-lg">
                      <div className="px-4 py-5 sm:p-6">
                        <div className="flex items-center">
                          <div className="flex-shrink-0 bg-indigo-500 rounded-md p-3">
                            <FileText className="h-6 w-6 text-white" />
                          </div>
                          <div className="ml-5 w-0 flex-1">
                            <dl>
                              <dt className="text-sm font-medium text-gray-500 truncate">Files Stored</dt>
                              <dd>
                                <div className="text-lg font-medium text-gray-900">
                                  {fileCount}
                                </div>
                              </dd>
                            </dl>
                          </div>
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Recent Activity */}
                  <div className="bg-white shadow overflow-hidden sm:rounded-lg">
                    <div className="px-4 py-5 sm:px-6">
                      <h3 className="text-lg leading-6 font-medium text-gray-900">
                        Recent Activity
                      </h3>
                      <p className="mt-1 max-w-2xl text-sm text-gray-500">
                        Your recent access requests and activities.
                      </p>
                    </div>
                    <div className="border-t border-gray-200">
                      <ul className="divide-y divide-gray-200">
                        {mockAccessRequests.map((request) => (
                          <li key={request.id}>
                            <div className="px-4 py-4 sm:px-6">
                              <div className="flex items-center justify-between">
                                <p className="text-sm font-medium text-indigo-600 truncate">
                                  {request.action} access to {request.resource}
                                </p>
                                <div className="ml-2 flex-shrink-0 flex">
                                  {request.status === 'pending' ? (
                                    <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-yellow-100 text-yellow-800">
                                      Pending
                                    </p>
                                  ) : request.status === 'approved' ? (
                                    <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                                      Approved
                                    </p>
                                  ) : (
                                    <p className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-red-100 text-red-800">
                                      Rejected
                                    </p>
                                  )}
                                </div>
                              </div>
                              <div className="mt-2 sm:flex sm:justify-between">
                                <div className="sm:flex">
                                  <p className="flex items-center text-sm text-gray-500">
                                    <Clock className="flex-shrink-0 mr-1.5 h-5 w-5 text-gray-400" />
                                    {new Date(request.timestamp).toLocaleString()}
                                  </p>
                                </div>
                              </div>
                            </div>
                          </li>
                        ))}
                      </ul>
                    </div>
                  </div>
                </div>
              )}
            </div>
          </div>
        </main>
      </div>
    </div>
  );
};

export default Dashboard;
