import React, { useState } from 'react';
import { 
  User, 
  Shield, 
  Activity, 
  LogOut, 
  Plus,
  Eye,
  Clock,
  CheckCircle,
  XCircle
} from 'lucide-react';
import { useWallet } from '../context/WalletContext';

const Dashboard: React.FC = () => {
  const { state, disconnectWallet, registerUser } = useWallet();
  // Registration form is always shown when user is not registered
  const [registrationData, setRegistrationData] = useState({ username: '', email: '' });
  const [isRegistering, setIsRegistering] = useState(false);

  // Debug logging
  console.log('Dashboard - isRegistered:', state.isRegistered, 'userProfile:', state.userProfile);

  const handleRegistration = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!registrationData.username || !registrationData.email) return;

    setIsRegistering(true);
    try {
      await registerUser(registrationData.username, registrationData.email);
      // Clear form data after successful registration
      setRegistrationData({ username: '', email: '' });
    } catch (error) {
      console.error('Registration failed:', error);
    } finally {
      setIsRegistering(false);
    }
  };

  const formatAddress = (address: string) => {
    return `${address.slice(0, 6)}...${address.slice(-4)}`;
  };

  const mockAccessRequests = [
    {
      id: 1,
      resource: 'Database',
      action: 'Read',
      timestamp: new Date().toISOString(),
      status: 'pending'
    },
    {
      id: 2,
      resource: 'API Gateway',
      action: 'Write',
      timestamp: new Date(Date.now() - 86400000).toISOString(),
      status: 'approved'
    },
    {
      id: 3,
      resource: 'File Storage',
      action: 'Delete',
      timestamp: new Date(Date.now() - 172800000).toISOString(),
      status: 'denied'
    }
  ];

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'approved':
        return <CheckCircle className="w-4 h-4 text-green-500" />;
      case 'denied':
        return <XCircle className="w-4 h-4 text-red-500" />;
      default:
        return <Clock className="w-4 h-4 text-yellow-500" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'approved':
        return 'text-green-400';
      case 'denied':
        return 'text-red-400';
      default:
        return 'text-yellow-400';
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-dark-900 via-dark-800 to-dark-900">
      {/* Header */}
      <header className="bg-dark-800 border-b border-dark-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center gap-3">
              <Shield className="w-8 h-8 text-primary-500" />
              <h1 className="text-xl font-bold text-white">BSCIAM Dashboard</h1>
            </div>
            
            <div className="flex items-center gap-4">
              <div className="text-right">
                <p className="text-white text-sm font-medium">
                  {state.userProfile?.username || 'Guest User'}
                </p>
                <p className="text-dark-400 text-xs">
                  {state.wallet.account ? formatAddress(state.wallet.account) : ''}
                </p>
              </div>
              <button
                onClick={disconnectWallet}
                className="p-2 text-dark-400 hover:text-white transition-colors"
              >
                <LogOut className="w-5 h-5" />
              </button>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Welcome Section */}
        <div className="mb-8">
          <h2 className="text-3xl font-bold text-white mb-2">
            {state.userProfile ? `Welcome back, ${state.userProfile.username}!` : 'Welcome to BSCIAM'}
          </h2>
          <p className="text-dark-300">
            {state.userProfile 
              ? `Your secure blockchain-based identity and access management dashboard`
              : 'Your secure blockchain-based identity and access management dashboard'
            }
          </p>
          {state.userProfile && (
            <div className="mt-4 p-4 bg-primary-900/20 border border-primary-500/30 rounded-lg">
              <p className="text-primary-300 text-sm">
                🎉 Great to see you again! Your reputation score is {state.userProfile.reputationScore} points.
              </p>
            </div>
          )}
        </div>

        {/* Stats Cards */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <div className="card">
            <div className="flex items-center gap-4">
              <div className="p-3 bg-primary-600 rounded-lg">
                <User className="w-6 h-6 text-white" />
              </div>
              <div>
                <p className="text-dark-400 text-sm">Reputation Score</p>
                <p className="text-2xl font-bold text-white">
                  {state.userProfile?.reputationScore || 100}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center gap-4">
              <div className="p-3 bg-green-600 rounded-lg">
                <Shield className="w-6 h-6 text-white" />
              </div>
              <div>
                <p className="text-dark-400 text-sm">Access Roles</p>
                <p className="text-2xl font-bold text-white">
                  {state.userProfile?.accessRoles.length || 0}
                </p>
              </div>
            </div>
          </div>

          <div className="card">
            <div className="flex items-center gap-4">
              <div className="p-3 bg-purple-600 rounded-lg">
                <Activity className="w-6 h-6 text-white" />
              </div>
              <div>
                <p className="text-dark-400 text-sm">Active Sessions</p>
                <p className="text-2xl font-bold text-white">1</p>
              </div>
            </div>
          </div>
        </div>

        {/* Registration Section */}
        {!state.isRegistered && (
          <div className="card mb-8 border-2 border-primary-500/30 bg-primary-900/10">
            <div className="text-center">
              <Shield className="w-16 h-16 text-primary-500 mx-auto mb-4 animate-pulse-slow" />
              <h3 className="text-2xl font-semibold text-white mb-2">Complete Your Registration</h3>
              <p className="text-dark-400 mb-6">
                Register with BSCIAM to access secure blockchain-based identity management
              </p>
              
              <div className="bg-yellow-900/20 border border-yellow-500/30 rounded-lg p-4 mb-6">
                <p className="text-yellow-300 text-sm">
                  ⚠️ You need to register with your name and email to use BSCIAM features
                </p>
              </div>
              
              <form onSubmit={handleRegistration} className="max-w-md mx-auto">
                <div className="space-y-4">
                  <div>
                    <label className="block text-left text-white text-sm font-medium mb-2">
                      Username *
                    </label>
                    <input
                      type="text"
                      placeholder="Enter your username"
                      value={registrationData.username}
                      onChange={(e) => setRegistrationData(prev => ({ ...prev, username: e.target.value }))}
                      className="w-full input"
                      required
                    />
                  </div>
                  <div>
                    <label className="block text-left text-white text-sm font-medium mb-2">
                      Email *
                    </label>
                    <input
                      type="email"
                      placeholder="Enter your email address"
                      value={registrationData.email}
                      onChange={(e) => setRegistrationData(prev => ({ ...prev, email: e.target.value }))}
                      className="w-full input"
                      required
                    />
                  </div>
                  <div className="flex gap-3">
                    <button
                      type="submit"
                      disabled={isRegistering || !registrationData.username || !registrationData.email}
                      className="flex-1 btn-primary disabled:opacity-50 disabled:cursor-not-allowed"
                    >
                      {isRegistering ? (
                        <>
                          <div className="w-4 h-4 border-2 border-white border-t-transparent rounded-full animate-spin mr-2" />
                          Registering...
                        </>
                      ) : (
                        <>
                          <Plus className="w-4 h-4 mr-2" />
                          Register Now
                        </>
                      )}
                    </button>
                  </div>
                </div>
              </form>
            </div>
          </div>
        )}

        {/* Access Requests */}
        <div className="card">
          <div className="flex items-center justify-between mb-6">
            <h3 className="text-xl font-semibold text-white">Recent Access Requests</h3>
            <button className="btn-primary inline-flex items-center gap-2">
              <Plus className="w-4 h-4" />
              New Request
            </button>
          </div>

          <div className="space-y-4">
            {mockAccessRequests.map((request) => (
              <div key={request.id} className="flex items-center justify-between p-4 bg-dark-700 rounded-lg">
                <div className="flex items-center gap-4">
                  <div className="p-2 bg-dark-600 rounded-lg">
                    <Eye className="w-5 h-5 text-primary-500" />
                  </div>
                  <div>
                    <p className="text-white font-medium">{request.resource}</p>
                    <p className="text-dark-400 text-sm">Action: {request.action}</p>
                  </div>
                </div>
                
                <div className="flex items-center gap-3">
                  <div className="text-right">
                    <p className="text-dark-400 text-sm">
                      {new Date(request.timestamp).toLocaleDateString()}
                    </p>
                    <div className="flex items-center gap-2">
                      {getStatusIcon(request.status)}
                      <span className={`text-sm font-medium ${getStatusColor(request.status)}`}>
                        {request.status.charAt(0).toUpperCase() + request.status.slice(1)}
                      </span>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>

      </div>
    </div>
  );
};

export default Dashboard;
