import React, { useState } from 'react';
import { Download, FileText, Image as ImageIcon, File, Key, Trash2 } from 'lucide-react';
import { decryptFile, saveDecryptedFile } from '../../utils/cryptoUtils';

export interface StoredFile {
  id: string;
  name: string;
  type: string;
  size: number;
  uploadedAt: string;
  encryptedData: string;
  metadata: {
    name: string;
    type: string;
    size: number;
    lastModified: number;
  };
}

interface FileListProps {
  files: StoredFile[];
  onDelete: (fileId: string) => Promise<void>;
}

const FileList: React.FC<FileListProps> = ({ files, onDelete }) => {
  const [decryptingFile, setDecryptingFile] = useState<string | null>(null);
  const [decryptionKey, setDecryptionKey] = useState('');
  const [activeFile, setActiveFile] = useState<string | null>(null);
  const [error, setError] = useState<string | null>(null);

  const getFileIcon = (fileType: string) => {
    if (fileType === 'application/pdf') {
      return <FileText className="w-5 h-5 text-red-500" />;
    }

    if (fileType.startsWith('image/')) {
      return <ImageIcon className="w-5 h-5 text-blue-500" />;
    }

    return <File className="w-5 h-5" />;
  };

  const formatFileSize = (bytes: number): string => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const handleDownload = async (file: StoredFile) => {
    setActiveFile(file.id);
    setError(null);
  };

  const confirmDownload = async () => {
    if (!activeFile || !decryptionKey) {
      setError('Please enter an encryption key');
      return;
    }

    try {
      setDecryptingFile(activeFile);

      const file = files.find(f => f.id === activeFile);
      if (!file) throw new Error('File not found');

      // Decrypt the file
      const decryptedBlob = await decryptFile(
        {
          encryptedData: file.encryptedData,
          metadata: {
            name: file.name,
            type: file.type,
            size: file.size,
            lastModified: new Date(file.uploadedAt).getTime()
          }
        },
        decryptionKey
      );

      // Save the decrypted file
      saveDecryptedFile(decryptedBlob, file.name);

      // Reset state
      setDecryptionKey('');
      setActiveFile(null);
      setError(null);
    } catch (err) {
      console.error('Download failed:', err);
      setError('Failed to decrypt file. Please check your encryption key.');
    } finally {
      setDecryptingFile(null);
    }
  };

  const cancelDownload = () => {
    setActiveFile(null);
    setDecryptionKey('');
    setError(null);
  };

  if (files.length === 0) {
    return (
      <div className="text-center py-12 bg-gray-50 rounded-lg">
        <File className="mx-auto h-12 w-12 text-gray-400" />
        <h3 className="mt-2 text-sm font-medium text-gray-900">No files</h3>
        <p className="mt-1 text-sm text-gray-500">
          Get started by uploading a new file.
        </p>
      </div>
    );
  }

  return (
    <div className="bg-white shadow overflow-hidden sm:rounded-md">
      <ul className="divide-y divide-gray-200">
        {files.map((file) => (
          <li key={file.id}>
            <div className="px-4 py-4 sm:px-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center">
                  <div className="flex-shrink-0">
                    {getFileIcon(file.type)}
                  </div>
                  <div className="ml-4">
                    <div className="text-sm font-medium text-indigo-600 truncate">
                      {file.name}
                    </div>
                    <div className="flex items-center text-sm text-gray-500">
                      <span>{formatFileSize(file.size)}</span>
                      <span className="mx-1">â€¢</span>
                      <span>{new Date(file.uploadedAt).toLocaleDateString()}</span>
                    </div>
                  </div>
                </div>
                <div className="ml-2 flex-shrink-0 flex space-x-2
                ">
                  <button
                    type="button"
                    onClick={() => handleDownload(file)}
                    disabled={!!decryptingFile}
                    className={`inline-flex items-center px-3 py-1.5 border border-transparent text-xs font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${decryptingFile ? 'opacity-50 cursor-not-allowed' : ''}`}
                  >
                    <Download className="w-3 h-3 mr-1" />
                    Download
                  </button>
                  <button
                    type="button"
                    onClick={() => onDelete(file.id)}
                    disabled={!!decryptingFile}
                    className="inline-flex items-center px-3 py-1.5 border border-gray-300 shadow-sm text-xs font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                  >
                    <Trash2 className="w-3 h-3 mr-1" />
                    Delete
                  </button>
                </div>
              </div>

              {activeFile === file.id && (
                <div className="mt-4 p-4 bg-gray-50 rounded-md">
                  <div className="mb-2 text-sm font-medium text-gray-700">
                    <Key className="inline w-4 h-4 mr-1" />
                    Enter encryption key for "{file.name}"
                  </div>
                  <div className="flex space-x-2">
                    <input
                      type="password"
                      className="flex-1 min-w-0 block w-full px-3 py-2 rounded-md border border-gray-300 shadow-sm focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                      placeholder="Enter encryption key"
                      value={decryptionKey}
                      onChange={(e) => setDecryptionKey(e.target.value)}
                    />
                    <button
                      type="button"
                      onClick={confirmDownload}
                      disabled={!decryptionKey || decryptingFile === file.id}
                      className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${(!decryptionKey || decryptingFile === file.id) ? 'opacity-50 cursor-not-allowed' : ''}`}
                    >
                      {decryptingFile === file.id ? (
                        <>
                          <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                            <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                            <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                          </svg>
                          Decrypting...
                        </>
                      ) : (
                        'Confirm'
                      )}
                    </button>
                    <button
                      type="button"
                      onClick={cancelDownload}
                      disabled={!!decryptingFile}
                      className="inline-flex items-center px-4 py-2 border border-gray-300 shadow-sm text-sm font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                    >
                      Cancel
                    </button>
                  </div>
                  {error && activeFile === file.id && (
                    <p className="mt-2 text-sm text-red-600">{error}</p>
                  )}
                </div>
              )}
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default FileList;
