import React, { useState, useEffect } from 'react';
import FileUpload from './FileUpload';
import FileList from './FileList';
import { StoredFile } from '../../types';
import { PlusIcon } from '@heroicons/react/24/outline';
import { useWallet } from '../../context/WalletContext';

interface FileManagerProps {
  walletAddress: string;
}

const FileManager: React.FC<FileManagerProps> = ({ walletAddress }) => {
  const { getStoredFiles, storeFile, deleteStoredFile } = useWallet();
  const [files, setFiles] = useState<StoredFile[]>([]);
  const [isUploading, setIsUploading] = useState(false);
  const [showUploadModal, setShowUploadModal] = useState(false);

  // Load user's files on component mount and when wallet address changes
  useEffect(() => {
    if (walletAddress) {
      const userFiles = getStoredFiles();
      setFiles(userFiles);
    } else {
      setFiles([]);
    }
  }, [walletAddress, getStoredFiles]);

  const handleFileUpload = async (file: File, encryptedData: any, key: string) => {
    if (!walletAddress) return;

    setIsUploading(true);

    try {
      const newFile: StoredFile = {
        id: Date.now().toString(),
        name: file.name,
        type: file.type,
        size: file.size,
        uploadedAt: new Date().toISOString(),
        encryptedData: encryptedData.encryptedData,
        metadata: {
          name: file.name,
          originalName: file.name,
          type: file.type,
          mimeType: file.type,
          size: file.size,
          lastModified: file.lastModified
        }
      };

      // Store the file using the wallet context
      storeFile(newFile);

      // Update local state
      setFiles(prevFiles => [...prevFiles, newFile]);

      // Close the upload modal
      setShowUploadModal(false);

    } catch (error) {
      console.error('File upload failed:', error);
      throw error;
    } finally {
      setIsUploading(false);
    }
  };

  const handleDeleteFile = async (fileId: string) => {
    if (!walletAddress) return;

    if (!window.confirm('Are you sure you want to delete this file? This action cannot be undone.')) {
      return;
    }

    try {
      // Delete the file using the wallet context
      deleteStoredFile(fileId);

      // Update local state
      setFiles(prevFiles => prevFiles.filter(file => file.id !== fileId));

    } catch (error) {
      console.error('Failed to delete file:', error);
    }
  };

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h2 className="text-2xl font-bold text-gray-900">My Files</h2>
        <button
          type="button"
          onClick={() => setShowUploadModal(true)}
          className="inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
        >
          <PlusIcon className="-ml-1 mr-2 h-5 w-5" />
          Upload File
        </button>
      </div>

      <div className="bg-white shadow rounded-lg p-6">
        <FileList 
          files={files} 
          onDelete={handleDeleteFile} 
        />
      </div>

      {/* Upload Modal */}
      {showUploadModal && (
        <div className="fixed z-10 inset-0 overflow-y-auto">
          <div className="flex items-center justify-center min-h-screen pt-4 px-4 pb-20 text-center sm:block sm:p-0">
            <div className="fixed inset-0 transition-opacity" aria-hidden="true">
              <div className="absolute inset-0 bg-gray-500 opacity-75"></div>
            </div>

            <span className="hidden sm:inline-block sm:align-middle sm:h-screen" aria-hidden="true">
              &#8203;
            </span>

            <div className="inline-block align-bottom bg-white rounded-lg text-left overflow-hidden shadow-xl transform transition-all sm:my-8 sm:align-middle sm:max-w-lg sm:w-full">
              <div className="bg-white px-4 pt-5 pb-4 sm:p-6 sm:pb-4">
                <div className="sm:flex sm:items-start">
                  <div className="mt-3 text-center sm:mt-0 sm:ml-4 sm:text-left w-full">
                    <h3 className="text-lg leading-6 font-medium text-gray-900 mb-4">
                      Upload File
                    </h3>
                    <div className="mt-2">
                      <FileUpload 
                        onUpload={handleFileUpload} 
                        isUploading={isUploading} 
                      />
                    </div>
                  </div>
                </div>
              </div>
              <div className="bg-gray-50 px-4 py-3 sm:px-6 sm:flex sm:flex-row-reverse">
                <button
                  type="button"
                  onClick={() => setShowUploadModal(false)}
                  className="mt-3 w-full inline-flex justify-center rounded-md border border-gray-300 shadow-sm px-4 py-2 bg-white text-base font-medium text-gray-700 hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 sm:mt-0 sm:ml-3 sm:w-auto sm:text-sm"
                  disabled={isUploading}
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default FileManager;
