import React, { useState, useRef, ChangeEvent } from 'react';
import { Upload, Key, X, FileText, Image as ImageIcon, File, AlertCircle } from 'lucide-react';
import { encryptFile } from '../../utils/cryptoUtils';
import { useWallet } from '../../context/WalletContext';

interface FileUploadProps {
  onUpload: (file: File, encryptedData: any, key: string) => Promise<void>;
  isUploading: boolean;
}

const FileUpload: React.FC<FileUploadProps> = ({ onUpload, isUploading }) => {
  const [selectedFile, setSelectedFile] = useState<File | null>(null);
  const [encryptionKey, setEncryptionKey] = useState('');
  const [isEncrypting, setIsEncrypting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [showKeyWarning, setShowKeyWarning] = useState(false);
  const fileInputRef = useRef<HTMLInputElement>(null);
  const { getEncryptionKey } = useWallet();

  const handleFileChange = (e: ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    // Only allow PDF and image files
    const validTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/gif'];
    if (!validTypes.includes(file.type)) {
      setError('Only PDF and image files are allowed');
      return;
    }

    setSelectedFile(file);
    setError(null);
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      setError('Please select a file');
      return;
    }

    // Check file size (max 10MB)
    if (selectedFile.size > 10 * 1024 * 1024) {
      setError('File size must be less than 10MB');
      return;
    }

    // Check file type
    const validTypes = ['application/pdf', 'image/jpeg', 'image/png', 'image/gif'];
    if (!validTypes.includes(selectedFile.type)) {
      setError('Only PDF, JPEG, PNG, and GIF files are allowed');
      return;
    }

    try {
      setIsEncrypting(true);
      setError(null);

      // Get or generate encryption key
      let key = encryptionKey.trim();
      if (!key) {
        const storedKey = getEncryptionKey();
        if (storedKey) {
          key = storedKey;
          setShowKeyWarning(true);
        } else {
          setError('Please enter an encryption key');
          return;
        }
      }

      // Encrypt the file
      const encryptedData = await encryptFile(selectedFile, key);

      // Call the parent's upload handler
      await onUpload(selectedFile, encryptedData, key);

      // Reset form
      setSelectedFile(null);
      setEncryptionKey('');
      setShowKeyWarning(false);
      if (fileInputRef.current) {
        fileInputRef.current.value = '';
      }
    } catch (err) {
      console.error('Upload failed:', err);
      setError('Failed to encrypt and upload file. Please try again.');
    } finally {
      setIsEncrypting(false);
    }
  };

  const getFileIcon = () => {
    if (!selectedFile) return <File className="w-6 h-6" />;

    if (selectedFile.type === 'application/pdf') {
      return <FileText className="w-6 h-6 text-red-500" />;
    }

    if (selectedFile.type.startsWith('image/')) {
      return <ImageIcon className="w-6 h-6 text-blue-500" />;
    }

    return <File className="w-6 h-6" />;
  };

  return (
    <div className="bg-white p-6 rounded-lg shadow-md">
      <h3 className="text-lg font-semibold mb-4 flex items-center">
        <Upload className="w-5 h-5 mr-2" />
        Upload File
      </h3>

      <div className="mb-4">
        <label className="block text-sm font-medium text-gray-700 mb-2">
          Select File (PDF or Image)
        </label>
        <div className="mt-1 flex justify-center px-6 pt-5 pb-6 border-2 border-gray-300 border-dashed rounded-md">
          <div className="space-y-1 text-center">
            {selectedFile ? (
              <div className="flex items-center justify-between p-2 bg-gray-50 rounded">
                <div className="flex items-center">
                  {getFileIcon()}
                  <span className="ml-2 text-sm text-gray-700 truncate max-w-xs">
                    {selectedFile.name}
                  </span>
                </div>
                <button
                  type="button"
                  onClick={() => setSelectedFile(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  <X className="w-4 h-4" />
                </button>
              </div>
            ) : (
              <>
                <div className="flex justify-center">
                  <Upload className="mx-auto h-12 w-12 text-gray-400" />
                </div>
                <div className="flex text-sm text-gray-600">
                  <label
                    htmlFor="file-upload"
                    className="relative cursor-pointer bg-white rounded-md font-medium text-indigo-600 hover:text-indigo-500 focus-within:outline-none focus-within:ring-2 focus-within:ring-offset-2 focus-within:ring-indigo-500"
                  >
                    <span>Upload a file</span>
                    <input
                      id="file-upload"
                      name="file-upload"
                      type="file"
                      className="sr-only"
                      onChange={handleFileChange}
                      ref={fileInputRef}
                      accept=".pdf,.jpg,.jpeg,.png,.gif"
                    />
                  </label>
                  <p className="pl-1">or drag and drop</p>
                </div>
                <p className="text-xs text-gray-500">
                  PDF, JPG, PNG, GIF up to 10MB
                </p>
              </>
            )}
          </div>
        </div>
      </div>

      <div className="mb-4">
        <label htmlFor="encryption-key" className="block text-sm font-medium text-gray-700 mb-1">
          <Key className="inline w-4 h-4 mr-1" />
          Encryption Key
        </label>
        <div className="mt-1">
          <input
            type="password"
            id="encryption-key"
            className="shadow-sm focus:ring-indigo-500 focus:border-indigo-500 block w-full sm:text-sm border-gray-300 rounded-md p-2 border"
            placeholder="Enter your encryption key"
            value={encryptionKey}
            onChange={(e) => setEncryptionKey(e.target.value)}
          />
        </div>
        <p className="mt-1 text-xs text-gray-500">
          {showKeyWarning ? (
            <span className="text-yellow-600 flex items-center">
              <AlertCircle className="w-4 h-4 mr-1" />
              Using your account's encryption key. For security, you can enter a different key.
            </span>
          ) : (
            'This key will be used to encrypt your file. Keep it safe as it\'s required for decryption.'
          )}
        </p>
      </div>

      {error && (
        <div className="mb-4 text-sm text-red-600">
          {error}
        </div>
      )}

      <div className="flex justify-end">
        <button
          type="button"
          onClick={handleUpload}
          disabled={!selectedFile || isUploading || isEncrypting}
          className={`inline-flex items-center px-4 py-2 border border-transparent text-sm font-medium rounded-md shadow-sm text-white bg-indigo-600 hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500 ${(!selectedFile || isUploading || isEncrypting) ? 'opacity-50 cursor-not-allowed' : ''}`}
        >
          {isUploading || isEncrypting ? (
            <>
              <svg className="animate-spin -ml-1 mr-2 h-4 w-4 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
              </svg>
              {isEncrypting ? 'Encrypting...' : 'Uploading...'}
            </>
          ) : (
            'Upload File'
          )}
        </button>
      </div>
    </div>
  );
};

export default FileUpload;
